function Carousel(a,b){this.id=a;this.$carousel=$("#"+a);this.carouselPureClone={};this.makeClone();if(b){this.init()}}Carousel.prototype.init=function(){this.$carousel.owlCarousel({navigation:false,slideSpeed:400,paginationSpeed:400,singleItem:true,autoPlay:5000,stopOnHover:true,lazyLoad:true,autoHeight:false,addClassActive:true,afterMove:function(b,a){a.find("img").resizeToParent("once")},afterLazyLoad:function(b,a){a.find("img").resizeToParent("once")}})};Carousel.prototype.reinit=function(){$("#"+this.id).replaceWith($(this.carouselPureClone));this.makeClone();this.$carousel=$("#"+this.id);this.init()};Carousel.prototype.makeClone=function(){this.carouselPureClone=document.getElementById(this.id).cloneNode(true)};Carousel.prototype.setAfterLoadFunction=function(a){this.$carousel.data("owlCarousel").reinit({afterLazyLoad:a})};Carousel.prototype.reconfigure=function(a){this.$carousel.data("owlCarousel").reinit(a)};